import React from 'react'
import { Link } from 'react-router-dom'
import { useQuery, gql } from '@apollo/client'
import Box from '@mui/material/Box'
import Grid from '@mui/material/Grid'
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';

const products = gql`
  query getProducts {
    products {
      data {
        attributes {
          price
          name
          description
          slug
          image {
            data {
              attributes {
                url
              }
            }
          }
        }
      }
    }
  }
`

const categories = gql`
  query getCategories {
    categories {
      data {
        attributes {
          name
        }
      }
    }
  }
`

export default function StoreFront(props) {
  const { loading, error, data } = useQuery(products)
  // const { loading1, error1, data } = useQuery(categories)

  if (loading) return <p>Loading...</p>
  if (error) return <p>{error}</p>
// console.log(error1)
  return (
    <Box sx={{ flexGrow: 1 }}>
      <Grid container spacing={3} alignItems="stretch">
        <Grid item xs={3} style={{ display: 'flex' }}>
          <List>
            {/* {cats.categories.data.map(cat =>
              <ListItem disablePadding>
                <ListItemButton>
                  <ListItemText primary={cat.name} />
                </ListItemButton>
              </ListItem>
            )} */}
          </List>
        </Grid>
        <Grid container item xs={9} spacing={3}>
          {data.products.data.map(prd =>
            <Grid key={prd.attributes.slug} item xs={3} style={{ display: 'flex' }}>
              <Card style={{ display: 'flex', justifyContent: 'space-between', flexDirection: 'column' }}>
                <CardMedia
                  component="img"
                  alt={prd.attributes.slug}
                  height="140"
                  image={`${props.baseURL}${prd.attributes.image.data ? prd.attributes.image.data.attributes.url : ''}`}
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="div">
                    {prd.attributes.name}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {prd.attributes.description}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button size="small">Buy Now</Button>
                  <Link to={`/product/${prd.attributes.slug}`}>
                    <Button size="small">Learn More</Button>
                  </Link>
                </CardActions>
              </Card>
            </Grid>
          )}
        </Grid>
      </Grid>
    </Box>
  )
}
